void MainScreenViewBase::setupScreen()
{
    CalibrationPage.initialize();
    keypad1.initialize();
}

void MainScreenViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &StartButton)
    {
        //Interaction19
        //When StartButton clicked call virtual function
        //Call StartBtnClicked
        StartBtnClicked();
    }
    else if (&src == &StopButton)
    {
        //Interaction20
        //When StopButton clicked call virtual function
        //Call StopBtnClicked
        StopBtnClicked();
    }
    else if (&src == &TimeSetButton)
    {
        //Interaction17
        //When TimeSetButton clicked call virtual function
        //Call TimeSetBtnClicked
        TimeSetBtnClicked();
    }
    else if (&src == &ElapsedTimeButton)
    {
        //Interaction16
        //When ElapsedTimeButton clicked call virtual function
        //Call ElapsedTimeBtnClicked
        ElapsedTimeBtnClicked();
    }
    else if (&src == &TimeModeBtn)
    {
        //Interaction21
        //When TimeModeBtn clicked call virtual function
        //Call TimeModeBtnPressed
        TimeModeBtnPressed();
    }
    else if (&src == &InfiniteModeBtn)
    {
        //Interaction22
        //When InfiniteModeBtn clicked call virtual function
        //Call InfiniteModeBtnPressed
        InfiniteModeBtnPressed();
    }
    else if (&src == &CalibrationPageButton)
    {
        //Interaction18
        //When CalibrationPageButton clicked call virtual function
        //Call CalibrationPageBtnClicked
        CalibrationPageBtnClicked();
    }
    else if (&src == &CVLevelButton)
    {
        //Interaction14
        //When CVLevelButton clicked call virtual function
        //Call CvLevelBtnClicked
        CvLevelBtnClicked();
    }
    else if (&src == &QuanchTimeButton)
    {
        //Interaction15
        //When QuanchTimeButton clicked call virtual function
        //Call QuanchTimeBtnClicked
        QuanchTimeBtnClicked();
    }
    else if (&src == &CCLevelButton)
    {
        //Interaction13
        //When CCLevelButton clicked call virtual function
        //Call CcLevelBtnClicked
        CcLevelBtnClicked();
    }
    else if (&src == &ARCLevelButton)
    {
        //Interaction12
        //When ARCLevelButton clicked call virtual function
        //Call ArcLevelBtnClicked
        ArcLevelBtnClicked();
    }
    else if (&src == &SetpointButton)
    {
        //Interaction11
        //When SetpointButton clicked call virtual function
        //Call SetPointBtnClicked
        SetPointBtnClicked();
    }
    else if (&src == &PowermodeButton)
    {
        //Interaction4
        //When PowermodeButton clicked animateToState slideMenu1
        //Set State on slideMenu1 to Collapsed
        slideMenu1.animateToState(SlideMenu::COLLAPSED);

        //Interaction5
        //When PowermodeButton clicked set text ModeText
        //Set ModeText text to Power Mode
        ModeText.setTypedText(touchgfx::TypedText(T_SINGLEUSEID78));
        ModeText.invalidate();
        ModeText.resizeToCurrentText();
        ModeText.invalidate();

        //Interaction10
        //When PowermodeButton clicked call virtual function
        //Call PowerModeFunc
        PowerModeFunc();
    }
    else if (&src == &VoltagemodeButton)
    {
        //Interaction3
        //When VoltagemodeButton clicked animateToState slideMenu1
        //Set State on slideMenu1 to Collapsed
        slideMenu1.animateToState(SlideMenu::COLLAPSED);

        //Interaction6
        //When VoltagemodeButton clicked set text ModeText
        //Set ModeText text to Voltage Mode
        ModeText.setTypedText(touchgfx::TypedText(T_SINGLEUSEID79));
        ModeText.invalidate();
        ModeText.resizeToCurrentText();
        ModeText.invalidate();

        //Interaction9
        //When VoltagemodeButton clicked call virtual function
        //Call VoltageModeFunc
        VoltageModeFunc();
    }
    else if (&src == &CurrentmodeButton)
    {
        //Interaction2
        //When CurrentmodeButton clicked animateToState slideMenu1
        //Set State on slideMenu1 to Collapsed
        slideMenu1.animateToState(SlideMenu::COLLAPSED);

        //Interaction7
        //When CurrentmodeButton clicked set text ModeText
        //Set ModeText text to Current Mode
        ModeText.setTypedText(touchgfx::TypedText(T_SINGLEUSEID80));
        ModeText.invalidate();
        ModeText.resizeToCurrentText();
        ModeText.invalidate();

        //Interaction8
        //When CurrentmodeButton clicked call virtual function
        //Call CurrentModeFunc
        CurrentModeFunc();
    }
    else if (&src == &ModeBox)
    {
        //Interaction1
        //When ModeBox clicked animateToState slideMenu1
        //Set State on slideMenu1 to Expanded
        slideMenu1.animateToState(SlideMenu::EXPANDED);
    }
}
